var monsters = require('origindb')('monsters');

module.exports = {

	makeStats: function(partylvl,pl,mob,mode){
		//HP
		var mobHP					 = monsters(mob).get('hp').split(',');
		var maxHP					 = Number(mobHP[(Number(partylvl) - 1)]) * Number(pl);
		if (mode == "pveffa") maxHP = Number(mobHP[(Number(partylvl) - 1)]) + 30 * Number(pl);
		
		//Magic
		var mobMag = monsters(mob).get('magic').split(',');
		var mag = Number(mobMag[(Number(partylvl) - 1)]);
		
		//Attack
		var mobAtk = monsters(mob).get('atk').split(',');
		var atk = Number(mobAtk[(Number(partylvl) - 1)]);
		
		//Pdef
		//var mobPdef = monsters(mob).get('pdef').split(',');
		//var pdef = Number(mobPdef[(Number(partylvl) - 1)]);
		
		//Peva
		var mobPeva = monsters(mob).get('peva').split(',');
		var peva = Number(mobPeva[(Number(partylvl) - 1)]);
		//var peva = Math.floor(Number(pdef)/10);	
		
		//Mdef
		//var mobMdef = monsters(mob).get('mdef').split(',');
		//var mdef = Number(mobMdef[(Number(partylvl) - 1)]);
		
		//Meva
		var mobMeva = monsters(mob).get('meva').split(',');
		var meva = Number(mobMeva[(Number(partylvl) - 1)]);
		//var meva = Math.floor(Number(mdef)/10); 
		
		//Mov
		var mobMov = monsters(mob).get('mov').split(',');
		var mov = Number(mobMov[(Number(partylvl) - 1)]);
		
		return [maxHP,mag,atk,meva,peva,mov];
	}
}